import { Component, OnInit } from '@angular/core';
import { SignupserviceService } from '../Services/signupservice.service';
import { NgForm } from '@angular/forms';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit{

  constructor(private service:SignupserviceService) {}
  dataitem:any={};
  backdata:any={};
  element:any={};

  ngOnInit(): void {

  }

  getdata(login:NgForm)
  {
    this.service.getdata(login.value).subscribe(data=>{
      console.log(data);
      this.backdata=data;
      for (let index = 0; index < this.backdata.length; index++) {
         this.element = this.backdata[index];
         console.log(this.element.password);
         console.log(this.dataitem.password);
         console.log(this.element.email);
         console.log(this.dataitem.email);

      if(this.element.email!=this.dataitem.email&&this.element.password!=this.dataitem.password)
      {
        alert("Wrong password");
        break;
      }
      else
      {
        alert("good");
        break;
      }
    }
    },
    (Error:any)=>
    {
      console.log(Error);
    }
    )
  }

}
<div class="container">
    <section class="gradient-custom">
        <div class="container pt-5 pb-5">
          <div class="row d-flex justify-content-center align-items-center h-100">
            <div class="col-12 col-md-8 col-lg-6 col-xl-5">
              <div class="card bg-dark text-white" style="border-radius: 1rem;">
                <div class="card-body p-5 text-center">

                  <form class="mb-md-5 mt-md-4 pb-5" #login="ngForm" (ngSubmit)="getdata(login)">

                    <h2 class="fw-bold mb-2 text-uppercase">Login</h2>
                    <p class="text-white-50 mb-5">Please enter your login and password!</p>

                    <div class="form-outline form-white mb-4">
                      <input type="email" id="typeEmailX" class="form-control form-control-lg" name="email" [(ngModel)]="dataitem.email" />
                      <label class="form-label" for="typeEmailX">Email</label>
                    </div>

                    <div class="form-outline form-white mb-4">
                      <input type="password" id="typePasswordX" class="form-control form-control-lg" name="password" [(ngModel)]="dataitem.password" />
                      <label class="form-label" for="typePasswordX">Password</label>
                    </div>

                    <p class="small mb-5 pb-lg-2"><a class="text-white-50" href="#!">Forgot password?</a></p>

                    <button class="btn btn-outline-light btn-lg px-5" type="submit">Login</button>

                    <div class="d-flex justify-content-center text-center mt-4 pt-1">
                      <a href="#!" class="text-white"><i class="fa fa-facebook-f fa-lg"></i></a>
                      <a href="#!" class="text-white"><i class="fa fa-twitter fa-lg mx-4 px-2"></i></a>
                      <a href="#!" class="text-white"><i class="fa fa-google fa-lg"></i></a>
                    </div>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
</div>
